__NUXT_JSONP__("/compsci-project", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p){return {data:[{post:{slug:"compsci-project",description:"ReadMe ist eine Anwendung, die ihre User zu einer bewussteren Interaktion mit Text bewegt. Als Museumsinstallation bringt sie dem Nutzer in ca. 1000 Zeichen die Grundinformationen zum NSA-Spionageskandal von 2013 näher.",title:"ReadMe",category:"compsci",meta_desc:"Interaction design casestudy expoloring attention span and information overload with a strong focus on storytelling",date:"winter 2020\u002F21",team:"solo",toc:[{id:e,depth:f,text:g},{id:h,depth:f,text:i}],body:{type:"root",children:[{type:b,tag:j,props:{id:e},children:[{type:b,tag:k,props:{ariaHidden:l,href:"#hintergrund",tabIndex:m},children:[{type:b,tag:n,props:{className:[o,p]},children:[]}]},{type:a,value:g}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Wie unaufmerksam unsere Interaktionen mit Text oftmals sein können, weiß wohl jeder, der sich selbst schon einmal selbst beim völlig gedankenlosen Scrollen durch Schlagzeilen oder Social Media erwischt hat."}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Aber auch im Analogen zeigen z.B. Museen, welchen Einfluss “Information Overload” auf unserer Aufnahmefähigkeit hat."}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"In Anbetracht der kurzen Zeit eines Museumsbesuchs und der dabei zu vermittelnden Fülle an Wissen ist es kaum verwunderlich, dass selbst die kürzesten Texte von Besuchern in der Regel bestenfalls knapp überflogen werden."}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Und auch wenn dieses Leseverhalten in der Natur einer Ausstellung liegt, ergibt sich die Problematik, dass diese äußerst oberflächlichen Interaktionen bestimmten Themen kaum gerecht werden können."}]},{type:a,value:c},{type:b,tag:j,props:{id:h},children:[{type:b,tag:k,props:{ariaHidden:l,href:"#konzept",tabIndex:m},children:[{type:b,tag:n,props:{className:[o,p]},children:[]}]},{type:a,value:i}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"ReadMe verknüpft daher das Lesen mit dem Prinzip von Beschädigung und Regeneration."}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Streicht der Nutzer beim Lesen unter den Worten entlang, so werden diese zunächst durch Verschlüsselung unkenntlich."}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Sobald allerdings der gesamte Text gelesen\u002F verschlüsselt wurde, löst sich die Verschlüsselung auf, um den nachfolgenden Textabschnitt freizugeben."}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"ReadMe erhöht somit die Komplexität der Interaktion des Lesensum diese dafür bewusster zu gestalten und der Ungeduld des Lesers entgegenzuwirken."}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Inhaltlich beschäftigt sich ReadMe mit den Leaks Edward Snowdens und dem daraus entstandenen NSA Spionageskandal von 2013."}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Angepasst an die Schnelllebigkeit des Museumskontextes wird ein grober Überblick über das Thema priorisiert und der Text in drei kurze Abschnitte unterteilt."}]}]},dir:"\u002Fprojects\u002Fmarkdown",path:"\u002Fprojects\u002Fmarkdown\u002Fcompsci-project",extension:".md",createdAt:"2021-09-13T10:06:31.598Z",updatedAt:"2021-09-20T13:42:35.731Z"}}],fetch:{},mutations:void 0}}("text","element","\n","p","hintergrund",2,"Hintergrund","konzept","Konzept","h2","a","true",-1,"span","icon","icon-link")));